INSERT INTO email_template (id, subject, body)
VALUES
    ('reservation_created_by_citizen', 'Vahvistus Espoon kaupungin venepaikan varauksesta', E'Hyvä asiakas,\n\nVeneelle varaamasi paikka on maksettu ja varaus on vahvistettu.\n\nVuokralainen:\n{{reserverName}}\n\nPaikan tiedot:\nPaikan nimi: {{name}}\nPaikan leveys {{width}}\nPaikan pituus {{length}}\nPaikan varuste/säilytystapa {{amenity}}\n\nVarauksesi on voimassa {{endDate}} asti.\n\nJos varasit laituripaikan, saat myöhemmin postissa kausitarran ja satamakartan, jossa avainkoodi laiturin portin avaimen teettämistä varten (Otsolahden F-laiturille ei ole porttia). Kausitarra tulee kiinnittää näkyvälle paikalle veneeseen tai jos varasit talvi- tai säilytyspaikan, suojatelttaan, traileriin tai pukkiin.\n\nJos varasit säilytyspaikan Ämmäsmäeltä:\nÄmmäsmäen kulkulätkän noudosta tulee sopia ennakkoon soittamalla numeroon 050 3209 681 arkisin kello 9-13. Kulkulätkä noudetaan Suomenojan satamasta (Hylkeenpyytäjäntie 9) maksukuittia näyttämällä.\n\nHallinnoi varauksiasi, veneitäsi ja omia tietojasi helposti osoitteessa https://varaukset.espoo.fi/kuntalainen/omat-tiedot.\n\nVenesatamia koskevat sopimusehdot ja säännöt sekä muuta infoa löydät osoitteesta https://www.espoo.fi/fi/liikunta-ja-luonto/veneily.\n\nTerveisin\nMerelliset ulkoilupalvelut\nvenepaikat@espoo.fi'),
    ('reservation_created_by_employee', 'Vahvistus Espoon kaupungin venepaikan varaus', E'Hyvä asiakas,\n\nSinulle on varattu espoon kaupungin paikka {{reservationDescription}}.\n\nSinulle on lähetetty lasku osoitteeseen {{invoiceAddress}}. Vahvistaaksesi varauksen, maksa paikka eräpäivään mennessä. Maksamaton paikka irtisanoutuu ja se vapautuu muiden varattavaksi. Maksun saavuttua tilillemme lähetämme sähköpostilla lisätietoa laiturin portin avaimesta sekä kausitarran postitse. Kausitarra tulee kiinnittää näkyvälle paikalle veneeseen, tai jos varasit talvi- tai säilytyspaikan, esim. suojatelttaan, traileriin tai pukkiin.\n\nVuokralainen:\n{{reserverName}}\n\nVenepaikan nimi {{name}}\nVenepaikan leveys {{width}}\nVenepaikan pituus {{length}}\nVenepaikan varustus {{amenity}}\nVarauksen voimassaolo päättyy {{endDate}} asti.\n\nJos varasit laituripaikan, saat myöhemmin postissa kausitarran ja satamakartan, jossa avainkoodi laiturin portin avaimen teettämistä varten (Otsolahden F-laiturille ei ole porttia). Kausitarra tulee kiinnittää näkyvälle paikalle veneeseen tai jos varasit talvi- tai säilytyspaikan, suojatelttaan, traileriin tai pukkiin.\n\nJos varasit säilytyspaikan Ämmäsmäeltä:\nÄmmäsmäen kulkulätkän noudosta tulee sopia ennakkoon soittamalla numeroon 050 3209 681 arkisin kello 9-13. Kulkulätkä noudetaan Suomenojan satamasta (Hylkeenpyytäjäntie 9) maksukuittia näyttämällä.\n\nHallinnoi varauksiasi, veneitäsi ja omia tietojasi helposti osoitteessa https://varaukset.espoo.fi/kuntalainen/omat-tiedot.\n\nVenesatamia koskevat sopimusehdot ja säännöt sekä muuta infoa löydät osoitteesta https://www.espoo.fi/fi/liikunta-ja-luonto/veneily.\n\nTerveisin\nMerelliset ulkoilupalvelut\nvenepaikat@espoo.fi'),
    ('reservation_renewed_by_citizen', 'Espoon kaupungin venepaikan jatkaminen', E'Hyvä asiakas,\n\nVeneelle varaamasi paikka on maksettu ja varaus on vahvistettu uudelle kaudelle.\n\nVuokralainen:\n{{reserverName}}\n\nPaikan tiedot:\nPaikan nimi: {{name}}\nPaikan leveys {{width}}\nPaikan pituus {{length}}\nPaikan varuste/säilytystapa {{amenity}}\n\nLähetämme kausitarran postitse.\n\nVarauksesi on voimassa {{endDate}} asti.\n\nHallinnoi varauksiasi, veneitäsi ja omia tietojasi helposti osoitteessa https://varaukset.espoo.fi/kuntalainen/omat-tiedot.\n\nVenesatamia koskevat sopimusehdot ja säännöt sekä muuta infoa löydät osoitteesta https://www.espoo.fi/fi/liikunta-ja-luonto/veneily.\n\nTerveisin\nMerelliset ulkoilupalvelut\nvenepaikat@espoo.fi'),
    ('reservation_renewed_by_employee', 'Espoon kaupungin venepaikan jatkaminen', E'Hyvä asiakas,\n\nVaraamasi Espoon kaupungin paikka {{reservationDescription}} on jatkettu uudelle kaudelle.\n\nSinulle on lähetetty lasku osoitteeseen {{invoiceAddress}}. Vahvistaaksesi varauksen, maksa paikka eräpäivään mennessä. Maksamaton paikka irtisanoutuu ja se vapautuu muiden varattavaksi.\n\nVuokralainen: {{reserverName}}\n\nVenepaikan nimi {{name}}\nVenepaikan leveys {{width}}\nVenepaikan pituus {{length}}\nVenepaikan varustus {{amenity}}\nVarauksen voimassaolo päättyy {{endDate}}\n\nHallinnoi varauksiasi, veneitäsi ja omia tietojasi helposti osoitteessa https://varaukset.espoo.fi/kuntalainen/omat-tiedot.\n\nVenesatamia koskevat sopimusehdot ja säännöt sekä muuta infoa löydät osoitteesta https://www.espoo.fi/fi/liikunta-ja-luonto/veneily.\n\nTerveisin\nMerelliset ulkoilupalvelut\nvenepaikat@espoo.fi');
